import React, { useEffect, useState } from "react";
import { AdminAPI } from "../../services/api";
import { toast } from "react-toastify";
import Loader from "../../components/common/Loader";

// --- PHẦN TASKBAR ADMIN (Tách ra thành component riêng bên ngoài) ---
const AdminTaskbar = () => {
  const location = useLocation();
  
  return (
    <div className="bg-white shadow-sm mb-4 p-3 rounded d-flex flex-wrap gap-2 align-items-center">
      <span className="fw-bold text-primary me-3">
        <i className="fas fa-user-shield me-2"></i>QUẢN TRỊ:
      </span>
      
      <Link to="/admin" className={`btn btn-sm ${location.pathname === "/admin" ? "btn-primary" : "btn-outline-primary"}`}>
        <i className="fas fa-tachometer-alt me-1"></i> Dashboard
      </Link>

      <Link to="/admin/products" className={`btn btn-sm ${location.pathname.includes("/products") ? "btn-success" : "btn-outline-success"}`}>
        <i className="fas fa-box-open me-1"></i> Sản phẩm
      </Link>

      <Link to="/admin/users" className={`btn btn-sm ${location.pathname.includes("/users") ? "btn-info text-white" : "btn-outline-info"}`}>
        <i className="fas fa-users me-1"></i> Người dùng
      </Link>

      <Link to="/admin/orders" className={`btn btn-sm ${location.pathname.includes("/orders") ? "btn-warning text-dark" : "btn-outline-warning text-dark"}`}>
        <i className="fas fa-clipboard-list me-1"></i> Đơn hàng
      </Link>

      <Link to="/admin/discounts" className={`btn btn-sm ${location.pathname.includes("/discounts") ? "btn-danger" : "btn-outline-danger"}`}>
        <i className="fas fa-tags me-1"></i> Mã giảm giá
      </Link>
    </div>
  );
};

const AdminUserList = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchUsers = async () => {
    try {
      const data = await AdminAPI.getUsers();
      setUsers(data);
    } catch (error) {
      toast.error("Lỗi tải danh sách người dùng");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const handleBlockUser = async (userId, isBlocked) => {
    if (!window.confirm(`Bạn có chắc muốn ${isBlocked ? "MỞ KHÓA" : "KHÓA"} người dùng này?`)) return;

    try {
      if (isBlocked) {
        await AdminAPI.unblockUser(userId);
        toast.success("Đã mở khóa tài khoản");
      } else {
        await AdminAPI.blockUser(userId);
        toast.warning("Đã khóa tài khoản");
      }
      fetchUsers(); // Reload lại danh sách
    } catch (error) {
      toast.error(error.message || "Lỗi cập nhật trạng thái");
    }
  };

  if (loading) return <Loader />;

  return (
    <div className="container mt-4">
      <h2 className="fw-bold text-primary mb-4"><i className="fas fa-users me-2"></i>Quản lý Người dùng</h2>
      
      <div className="card shadow-sm border-0">
        <div className="card-body p-0">
          <div className="table-responsive">
            <table className="table table-hover mb-0 align-middle">
              <thead className="bg-light">
                <tr>
                  <th className="ps-4">Tên người dùng</th>
                  <th>Email</th>
                  <th>Vai trò</th>
                  <th>Điểm thưởng</th>
                  <th>Trạng thái</th>
                  <th className="text-end pe-4">Hành động</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user._id}>
                    <td className="ps-4 fw-bold">
                      <div className="d-flex align-items-center gap-2">
                        <div className="bg-secondary rounded-circle text-white d-flex justify-content-center align-items-center" style={{width: 30, height: 30}}>
                          {user.name.charAt(0).toUpperCase()}
                        </div>
                        {user.name}
                      </div>
                    </td>
                    <td>{user.email}</td>
                    <td>
                      {user.isAdmin ? (
                        <span className="badge bg-danger">Admin</span>
                      ) : (
                        <span className="badge bg-info text-dark">Khách hàng</span>
                      )}
                    </td>
                    <td className="text-warning fw-bold">{user.loyaltyPoints || 0} điểm</td>
                    <td>
                      {user.isBlocked ? (
                        <span className="badge bg-secondary">Đã khóa</span>
                      ) : (
                        <span className="badge bg-success">Hoạt động</span>
                      )}
                    </td>
                    <td className="text-end pe-4">
                      {!user.isAdmin && ( // Không cho phép khóa Admin
                        <button 
                          className={`btn btn-sm ${user.isBlocked ? "btn-success" : "btn-outline-danger"}`}
                          onClick={() => handleBlockUser(user._id, user.isBlocked)}
                          title={user.isBlocked ? "Mở khóa" : "Khóa tài khoản"}
                        >
                          {user.isBlocked ? <i className="fas fa-unlock"></i> : <i className="fas fa-lock"></i>}
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminUserList;
