import React, { useEffect, useState } from "react";
import { AdminAPI } from "../../services/api";
import { toast } from "react-toastify";
import Loader from "../../components/common/Loader";


<AdminTaskbar/>

const AdminDiscountList = () => {
  const [discounts, setDiscounts] = useState([]);
  const [loading, setLoading] = useState(true);
  
  // State cho form tạo mới
  const [newCode, setNewCode] = useState({ code: "", value: 0, usageLimit: 10 });
  const [showModal, setShowModal] = useState(false);

  // Load danh sách mã giảm giá
  const fetchDiscounts = async () => {
    try {
      const res = await AdminAPI.getDiscounts(); // Cần đảm bảo API này có trong api.js (nếu chưa có thì thêm vào)
      // Backend trả về { discounts: [...] }
      setDiscounts(res.discounts || []);
    } catch (error) {
      toast.error("Lỗi tải danh sách mã giảm giá");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Hàm giả lập gọi API nếu chưa có trong api.js, bạn cần update api.js sau
    // Tạm thời dùng fetch trực tiếp hoặc thêm vào api.js: getAllDiscountCodes
    fetchDiscounts();
  }, []);

  const handleCreateDiscount = async (e) => {
    e.preventDefault();
    if (!newCode.code || newCode.value <= 0) {
      return toast.warning("Vui lòng nhập đầy đủ thông tin hợp lệ");
    }

    try {
      await AdminAPI.createDiscount(newCode); // Cần thêm vào api.js
      toast.success("Tạo mã giảm giá thành công!");
      setShowModal(false);
      setNewCode({ code: "", value: 0, usageLimit: 10 });
      fetchDiscounts(); // Load lại danh sách
    } catch (error) {
      toast.error(error.message || "Lỗi tạo mã giảm giá");
    }
  };

  if (loading) return <Loader />;

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fw-bold text-primary"><i className="fas fa-tags me-2"></i>Quản lý Mã giảm giá</h2>
        <button 
          className="btn btn-success"
          onClick={() => setShowModal(true)}
        >
          <i className="fas fa-plus me-2"></i>Tạo mã mới
        </button>
      </div>

      <div className="card shadow-sm border-0">
        <div className="card-body p-0">
          <table className="table table-hover mb-0 align-middle">
            <thead className="bg-light">
              <tr>
                <th className="ps-4">Mã Code</th>
                <th>Giá trị giảm</th>
                <th>Giới hạn dùng</th>
                <th>Đã dùng</th>
                <th>Trạng thái</th>
                <th>Ngày tạo</th>
              </tr>
            </thead>
            <tbody>
              {discounts.length > 0 ? (
                discounts.map((d) => (
                  <tr key={d._id}>
                    <td className="ps-4 fw-bold text-primary">{d.code}</td>
                    <td><span className="badge bg-info text-dark">{d.value}%</span></td>
                    <td>{d.usageLimit}</td>
                    <td>{d.usedCount}</td>
                    <td>
                      {d.usedCount >= d.usageLimit ? (
                        <span className="badge bg-secondary">Hết lượt</span>
                      ) : (
                        <span className="badge bg-success">Đang hoạt động</span>
                      )}
                    </td>
                    <td>{new Date(d.createdAt).toLocaleDateString("vi-VN")}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="text-center py-4 text-muted">Chưa có mã giảm giá nào.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Modal Tạo Mã Giảm Giá (Dùng Bootstrap Modal HTML thuần hoặc state) */}
      {showModal && (
        <div className="modal d-block" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Tạo Mã Giảm Giá Mới</h5>
                <button type="button" className="btn-close" onClick={() => setShowModal(false)}></button>
              </div>
              <div className="modal-body">
                <form onSubmit={handleCreateDiscount}>
                  <div className="mb-3">
                    <label className="form-label">Mã Code (Ví dụ: SALE50)</label>
                    <input 
                      type="text" 
                      className="form-control text-uppercase" 
                      value={newCode.code}
                      onChange={(e) => setNewCode({...newCode, code: e.target.value.toUpperCase()})}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Phần trăm giảm (%)</label>
                    <input 
                      type="number" 
                      className="form-control" 
                      value={newCode.value}
                      min="1" max="100"
                      onChange={(e) => setNewCode({...newCode, value: e.target.value})}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">Số lượt sử dụng tối đa</label>
                    <input 
                      type="number" 
                      className="form-control" 
                      value={newCode.usageLimit}
                      min="1"
                      onChange={(e) => setNewCode({...newCode, usageLimit: e.target.value})}
                      required
                    />
                  </div>
                  <div className="d-flex justify-content-end gap-2">
                    <button type="button" className="btn btn-secondary" onClick={() => setShowModal(false)}>Hủy</button>
                    <button type="submit" className="btn btn-primary">Tạo mã</button>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDiscountList;
