import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { AdminAPI, ProductAPI } from "../../services/api";
import { toast } from "react-toastify";
import Loader from "../../components/common/Loader";

const ProductEdit = () => {
  const { id } = useParams(); // Nếu có ID -> Mode Edit, không có -> Mode Create
  const navigate = useNavigate();
  const isEditMode = !!id;

  const [loading, setLoading] = useState(isEditMode);
  const [categories, setCategories] = useState([]); // Cần lấy danh sách danh mục để chọn
  
  const [formData, setFormData] = useState({
    name: "",
    slug: "",
    brand: "",
    category: "",
    price: 0,
    description: "",
    images: [], // Mảng chứa link ảnh (string)
    imageUrlInput: "" // Input tạm để add link ảnh
  });

  useEffect(() => {
    const initData = async () => {
      try {
        // 1. Lấy danh mục (Giả sử có API getCategories)
        const cats = await AdminAPI.getCategories(); 
        setCategories(cats);

        // 2. Nếu là Edit, lấy chi tiết sản phẩm
        if (isEditMode) {
          const product = await ProductAPI.getDetail(id);
          setFormData({
            name: product.name,
            slug: product.slug,
            brand: product.brand,
            category: product.category?._id || product.category, // Handle populate
            price: product.price,
            description: product.description,
            images: product.images.map(img => typeof img === 'string' ? img : img.url),
            imageUrlInput: ""
          });
        }
      } catch (error) {
        toast.error("Lỗi tải dữ liệu");
      } finally {
        setLoading(false);
      }
    };
    initData();
  }, [id, isEditMode]);

  // Xử lý nhập liệu
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Thêm link ảnh vào mảng
  const handleAddImage = () => {
    if (!formData.imageUrlInput) return;
    setFormData({
      ...formData,
      images: [...formData.images, formData.imageUrlInput],
      imageUrlInput: ""
    });
  };

  // Xóa ảnh
  const handleRemoveImage = (index) => {
    const newImages = formData.images.filter((_, i) => i !== index);
    setFormData({ ...formData, images: newImages });
  };

  // Submit form
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const payload = { ...formData };
      delete payload.imageUrlInput; // Bỏ trường thừa

      if (isEditMode) {
        await AdminAPI.updateProduct(id, payload);
        toast.success("Cập nhật sản phẩm thành công");
      } else {
        await AdminAPI.addProduct(payload);
        toast.success("Tạo sản phẩm mới thành công");
      }
      navigate("/admin/products");
    } catch (error) {
      toast.error(error.message || "Lỗi lưu sản phẩm");
    }
  };

  if (loading) return <Loader />;

  return (
    <div className="container mt-4 mb-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fw-bold text-primary">
          {isEditMode ? "Chỉnh sửa sản phẩm" : "Thêm sản phẩm mới"}
        </h2>
        <button className="btn btn-outline-secondary" onClick={() => navigate("/admin/products")}>
          Quay lại
        </button>
      </div>

      <div className="card shadow-sm p-4">
        <form onSubmit={handleSubmit}>
          <div className="row">
            {/* Cột trái: Thông tin cơ bản */}
            <div className="col-md-8">
              <div className="mb-3">
                <label className="form-label fw-bold">Tên sản phẩm</label>
                <input type="text" className="form-control" name="name" value={formData.name} onChange={handleChange} required />
              </div>
              
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label fw-bold">Slug (URL)</label>
                  <input type="text" className="form-control" name="slug" value={formData.slug} onChange={handleChange} required />
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label fw-bold">Thương hiệu</label>
                  <input type="text" className="form-control" name="brand" value={formData.brand} onChange={handleChange} required />
                </div>
              </div>

              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label fw-bold">Danh mục</label>
                  <select className="form-select" name="category" value={formData.category} onChange={handleChange} required>
                    <option value="">-- Chọn danh mục --</option>
                    {categories.map(cat => (
                      <option key={cat._id} value={cat._id}>{cat.name}</option>
                    ))}
                  </select>
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label fw-bold">Giá gốc (VNĐ)</label>
                  <input type="number" className="form-control" name="price" value={formData.price} onChange={handleChange} required />
                </div>
              </div>

              <div className="mb-3">
                <label className="form-label fw-bold">Mô tả chi tiết</label>
                <textarea className="form-control" rows="5" name="description" value={formData.description} onChange={handleChange}></textarea>
              </div>
            </div>

            {/* Cột phải: Hình ảnh */}
            <div className="col-md-4">
              <label className="form-label fw-bold">Hình ảnh sản phẩm</label>
              <div className="input-group mb-2">
                <input 
                  type="text" 
                  className="form-control" 
                  placeholder="Dán link ảnh vào đây..." 
                  name="imageUrlInput"
                  value={formData.imageUrlInput}
                  onChange={handleChange}
                />
                <button type="button" className="btn btn-secondary" onClick={handleAddImage}>Thêm</button>
              </div>
              
              <div className="d-flex flex-wrap gap-2 mt-3">
                {formData.images.map((url, idx) => (
                  <div key={idx} className="position-relative" style={{width: "80px", height: "80px"}}>
                    <img src={url} alt="" className="w-100 h-100 object-fit-cover rounded border" />
                    <button 
                      type="button"
                      className="btn btn-sm btn-danger position-absolute top-0 end-0 p-0" 
                      style={{width: "20px", height: "20px", lineHeight: "10px"}}
                      onClick={() => handleRemoveImage(idx)}
                    >
                      &times;
                    </button>
                  </div>
                ))}
              </div>
              <small className="text-muted d-block mt-2">
                * Sử dụng link ảnh online (ví dụ từ Unsplash, Imgur...) để đảm bảo hiển thị tốt nhất.
              </small>
            </div>
          </div>

          <hr />
          <div className="d-flex justify-content-end">
            <button type="submit" className="btn btn-primary px-5 py-2 fw-bold">
              {isEditMode ? "Cập nhật" : "Tạo mới"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProductEdit;
