// src/app.js
(function() {
    const view = document.getElementById('view'); // <div id="view"></div> trong publics/index.html
    const nav = document.getElementById('nav'); // nếu bạn có header/nav

    // Khai báo routes → module loader trả về object { mount(root, params) }
    function App() {
        return ( <
            Routes >
            <
            Route path = "/"
            element = { < LandingPage / > }
            /> <
            Route path = "/catalog"
            element = { < CatalogPage / > }
            /> <
            Route path = "/product/:id"
            element = { < ProductDetail / > }
            /> <
            Route path = "/cart"
            element = { < CartPage / > }
            /> <
            Route path = "/checkout"
            element = { < CheckoutPage / > }
            /> <
            Route path = "/profile"
            element = { < ProfilePage / > }
            /> <
            Route path = "/login"
            element = { < LoginPage / > }
            /> <
            Route path = "/register"
            element = { < RegisterPage / > }
            /> <
            /Routes>
        );
    }

    function parseHash() {
        const hash = location.hash || '#/landing';
        const [path, ...rest] = hash.slice(1).split('/'); // ['','product','123'] -> careful
        // build route & params
        const segs = hash.slice(1).split('/');
        // segs[0] === '' do hash starts with '#/'
        const base = `/${segs[1] || ''}`;
        const params = { id: segs[2] };
        return { route: base || '/landing', params };
    }

    async function render() {
        const { route, params } = parseHash();
        const loader = routes[route] || routes['/'];
        view.innerHTML = `<div class="p-4 text-sm text-gray-500">Đang tải...</div>`;
        try {
            const page = await loader();
            await page.mount(view, params);
        } catch (e) {
            console.error(e);
            view.innerHTML = `<div class="p-4 text-red-600">Lỗi tải trang: ${e.message}</div>`;
        }
    }

    window.addEventListener('hashchange', render);
    window.addEventListener('DOMContentLoaded', render);
})();
